package kr.jiasoft.hiteen.feature.asset

import org.junit.jupiter.api.Assertions.assertNotNull
import org.junit.jupiter.api.Test
import java.io.ByteArrayInputStream
import javax.imageio.ImageIO

/**
 * 서버 환경에서 간헐적으로 ImageIO JPEG reader가 없어 Thumbnailator가 실패하는 케이스를 방지하기 위한 스모크 테스트.
 * TwelveMonkeys ImageIO 플러그인이 클래스패스에 있으면 통과합니다.
 */
class ImageIoSmokeTest {

    @Test
    fun `ImageIO can read a jpeg`() {
        // 1x1 흰색 JPEG (base64)
        val base64 = ""
        val bytes = java.util.Base64.getDecoder().decode(base64)

        val img = ImageIO.read(ByteArrayInputStream(bytes))
        assertNotNull(img)
    }
}

